import React, {useRef, useState} from 'react'
import './Video.css'
import VideoFooter from './VideoFooter'

function Video() {
    const [playing, setPlaying] = useState(false);
    const videoRef = useRef(null)

    const onVideoPress = () => {
        if (playing) {
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);
        }
    }

    return (
        <div className="video">
            <video 
                className="video__player" 
                loop
                onClick={onVideoPress}
                ref={videoRef}
                src="https://v77.tiktokcdn.com/d47829f7890a1ba09b806dc2dbb782ec/5ffed3dd/video/tos/useast2a/tos-useast2a-pve-0068/568a37357e8b4418a336fc5c93fb3844/?a=1233&br=2170&bt=1085&cd=0%7C0%7C1&cr=0&cs=0&cv=1&dr=0&ds=3&er=&l=202101130504480101890560344D01015C&lr=tiktok_m&mime_type=video_mp4&qs=0&rc=MzM4MzZ2bHQzeTMzOTczM0ApO2llODRlZjszN2Y4aWk5OWcybHBqbzAtampfLS1eMTZzc2NiNjUxMl5fYjFiMjYwNi06Yw%3D%3D&vl=&vr="
            ></video>
            <VideoFooter/>
        </div>
    )
}

export default Video
