import React, {useRef, useState} from 'react'
import './Video.css'
import VideoFooter from './VideoFooter'
import VideoSidebar from './VideoSidebar'

function Video() {
    const [playing, setPlaying] = useState(false);
    const videoRef = useRef(null)

    const onVideoPress = () => {
        if (playing) {
            videoRef.current.pause();
            setPlaying(false);
        } else {
            videoRef.current.play();
            setPlaying(true);
        }
    }

    return (
        <div className="video">
            <video 
                className="video__player" 
                loop
                onClick={onVideoPress}
                ref={videoRef}
                src="https://v77.tiktokcdn.com/3b4f9cdd0857474805a76331e0bd9a75/5fff35a9/video/tos/alisg/tos-alisg-pve-0037c001/fb8f75720d264d4da015c5b651792f9d/?a=1233&br=6318&bt=3159&cd=0%7C0%7C1&cr=0&cs=0&cv=1&dr=0&ds=3&er=&l=20210113120203010190176045170ACD51&lr=tiktok_m&mime_type=video_mp4&qs=0&rc=M2dtZHF2OXhqeDMzOzczM0ApZzplOjdnZDxoN2c2Z2czNGdwXm5wZ2ozLm5fLS02MTRzczFeLTM2Xi9hMC0zYTQtYjM6Yw%3D%3D&vl=&vr="
            ></video>
            <VideoFooter 
                channel="rafeqazi" 
                description="This is best" 
                song="Alish Usmonov"/>
            <VideoSidebar/>
        </div>
    )
}

export default Video
